import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import { getAuth } from 'firebase/auth';
import { getFirestore, collection, doc, addDoc } from 'firebase/firestore';

export default function AddCard() {
  const [issuer, setIssuer] = useState('');
  const [cardNumber, setCardNumber] = useState('');

  const handleAddCard = async () => {
    try {
      // Get the currently authenticated user
      const auth = getAuth();
      const user = auth.currentUser;

      // Ensure the user is authenticated before adding a card
      if (!user) {
        // Handle the case where the user is not authenticated, e.g., show an error message
        return;
      }

      // Create a reference to the 'cards' collection in Firestore
      const db = getFirestore();
      const cardsCollection = collection(db, 'cards');

      // Define the card data
      const cardData = {
        issuer,
        cardNumber,
        userId: user.uid, // Associate the card with the user
      };

      // Add the card data to Firestore
      const docRef = await addDoc(cardsCollection, cardData);

      // Clear the input fields after adding the card
      setIssuer('');
      setCardNumber('');

      // You can also handle success or show a confirmation message
    } catch (error) {
      // Handle errors, e.g., show an error message
      console.error('Error adding card:', error);
    }
  };

  return (
    <View style={styles.container}>
      <Text>Add Card</Text>
      <TextInput
        style={styles.input}
        placeholder="Issuer"
        value={issuer}
        onChangeText={(text) => setIssuer(text)}
      />
      <TextInput
        style={styles.input}
        placeholder="Card Number"
        value={cardNumber}
        onChangeText={(text) => setCardNumber(text)}
      />
      <Button title="Add Card" onPress={handleAddCard} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 12,
    borderRadius: 10,
    marginVertical: 8,
  },
});
