import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';

export default function AddCard() {
  const [issuer, setIssuer] = useState('');
  const [cardNumber, setCardNumber] = useState('');

  const handleAddCard = () => {
    // Implement card addition logic here
    // You can add the card data to your user's account
    // Make sure to validate the card information before saving it

    // Clear the input fields after adding the card
    setIssuer('');
    setCardNumber('');
  };

  return (
    <View style={styles.container}>
      <Text>Add Card</Text>
      <TextInput
        style={styles.input}
        placeholder="Issuer"
        value={issuer}
        onChangeText={(text) => setIssuer(text)}
      />
      <TextInput
        style={styles.input}
        placeholder="Card Number"
        value={cardNumber}
        onChangeText={(text) => setCardNumber(text)}
      />
      <Button title="Add Card" onPress={handleAddCard} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 12,
    borderRadius: 10,
    marginVertical: 8,
  },
});
