import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { db } from '../firebaseConfig';
const CardIssuerSelector = () => {
  const [issuer, setIssuer] = useState('');

  useEffect(() => {
    // Fetch data from Firebase Realtime Database
    const databaseRef = db.ref('Sheet1'); // Update the reference to match your data structure

    databaseRef.once('value', (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();

        // Assuming you want to get the "Issuer" from the first item in the data
        if (data[0] && data[0].Issuer) {
          setIssuer(data[0].Issuer);
        }
      }
    });
  }, []);

  return (
    <View style={styles.container}>
      <Text>Issuer: {issuer}</Text>
      {/* Add your UI components and use the "issuer" state as needed */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default CardIssuerSelector;
