import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Alert } from 'react-native';
import { createUserWithEmailAndPassword, getAuth } from 'firebase/auth';
import { collection, doc, setDoc, getFirestore } from 'firebase/firestore';
import { auth } from './Firebase'; // Import Firebase authentication object

export default function SignupScreen({ navigation }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignup = async () => {
    try {
      const emailToLowerCase = email.toLowerCase();
      await createUserWithEmailAndPassword(auth, emailToLowerCase, password);

      const userData = {
        email: emailToLowerCase,
        // Add more user properties as needed
      };

      const db = getFirestore();
      const usersCollection = collection(db, 'users');
      await setDoc(doc(usersCollection, auth.currentUser.uid), userData);

      // After successful signup, navigate to the login screen
      navigation.navigate('Login');
    } catch (error) {
      console.error(error.message);
      Alert.alert('Error', 'An error occurred during registration.');
    }
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign Up</Text>
      <TextInput
        style={styles.input}
        placeholder="Email"
        onChangeText={setEmail}
        value={email}
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        onChangeText={setPassword}
        value={password}
        secureTextEntry
      />
      <View style={styles.signupBox}>
        <Button title="Sign Up" onPress={handleSignup} color="white" />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightgray',
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingLeft: 10,
    borderRadius: 5,
  },
  signupBox: {
    width: 200,
    height: 50,
    backgroundColor: 'blue',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 10,
  },
});
